import React, { useState, useEffect } from 'react';
import styles from './Swap.module.css';
import ButtonComponent from './button';
import RubLogo from './rub.svg';
import RmbLogo from './rmb.svg'; // Импортируем иконку RMB
import UsdLogo from './usd.svg'; // Импортируем иконку USD

// Установим фиксированные курсы
const RMB_TO_RUB_RATE = 14.11;
const USD_TO_RMB_RATE = 7.05;

function Swap() {
    const [fromValue, setFromValue] = useState(0.0);
    const [toValue, setToValue] = useState(0.0);
    const [currency, setCurrency] = useState('RMB'); // Добавляем состояние для выбора валюты

    useEffect(() => {
        setFromValue(0.0);
        setToValue(0.0);
    }, []);

    const handleFromInputChange = (e) => {
        const value = parseFloat(e.target.value) || 0.0;
        setFromValue(value);
        if (currency === 'RMB') {
            setToValue((value * RMB_TO_RUB_RATE).toFixed(2));
        } else if (currency === 'USD') {
            setToValue((value * USD_TO_RMB_RATE * RMB_TO_RUB_RATE).toFixed(2));
        }
    };

    const handleToInputChange = (e) => {
        const value = parseFloat(e.target.value) || 0.0;
        setToValue(value);
        if (currency === 'RMB') {
            setFromValue((value / RMB_TO_RUB_RATE).toFixed(2));
        } else if (currency === 'USD') {
            setFromValue((value / (USD_TO_RMB_RATE * RMB_TO_RUB_RATE)).toFixed(2));
        }
    };

    return (
        <div className={styles.swap}>
            <div className={styles.currencyPair}>
                <div className={styles.currencyPairItem}>
                    <h3>To</h3>
                    <div className={styles.swapInput}>
                        <div className={styles.swapInputLeft}>
                            <input value={toValue} onChange={handleToInputChange} />
                        </div>
                        <div className={styles.swapInputRight}>
                            <div className={styles.swapInputPlaceholder}>~RUB</div>
                            <button className={styles.swapInputButton}>
                                <img src={RubLogo} alt="RUB" />
                                RUB
                            </button>
                        </div>
                    </div>
                </div>
                <div className={styles.currencyPairItem}>
                    <h3>From</h3>
                    <div className={styles.swapInput}>
                        <div className={styles.swapInputLeft}>
                            <input value={fromValue} onChange={handleFromInputChange} />
                        </div>
                        <div className={styles.swapInputRight}>
                            <div className={styles.swapInputPlaceholder}>
                                ~{currency}
                            </div>
                            <select
                                className={styles.swapInputButton}
                                value={currency}
                                onChange={(e) => setCurrency(e.target.value)}
                            >
                                <option value="RMB">
                                    <img src={RmbLogo} alt="RMB" />
                                    RMB
                                </option>
                                <option value="USD">
                                    <img src={UsdLogo} alt="USD" />
                                    USD
                                </option>
                            </select>
                        </div>
                    </div>
                </div>
            </div>
            <div className={styles.aboutSwap}>
                <div className={styles.aboutSwapItem}>
                    <div className={styles.aboutSwapItemLeft}>
                        Цена:
                    </div>
                    <div className={styles.aboutSwapItemRight}>
                        {currency === 'RMB' ? `${RMB_TO_RUB_RATE} RUB per 1 RMB` : `${USD_TO_RMB_RATE} RMB per 1 USD`}
                    </div>
                </div>
            </div>
            <ButtonComponent className={styles.swapButton}>Swap</ButtonComponent>
        </div>
    );
}

export default Swap;
