import React, { useState } from 'react';
import RmbLogo from './path/to/rmbLogo'; // Иконка RMB
import UsdLogo from './path/to/usdLogo'; // Иконка USD
import styles from './swap.module.css'; // Импортируем модульные стили

const Swap = ({ ruB, conversionResult }) => {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    return (
        <div style={{ position: 'relative' }}>
            {/* Строка To остается неизменной */}
            <div>
                To: {ruB}
            </div>

            {/* Строка From с новыми данными */}
            <div>
                From: 
                <div onClick={toggleDropdown} style={{ display: 'inline-block', cursor: 'pointer' }}>
                    <RmbLogo className={styles.currencyIcon} /> {/* Иконка RMB */}
                    RMB
                </div>

                {conversionResult} {/* Результат конвертации */}

                {/* Выпадающий список */}
                {isDropdownOpen && (
                    <div className={styles.dropdown}>
                        <div className={styles.dropdownItem} onClick={() => { /* Обработчик клика для выбора USD */ }}>
                            <UsdLogo className={styles.currencyIcon} />
                            USD
                        </div>
                    </div>
                )}
            </div>

            <div>
                Цена: 14.11 RUB per 1 RMB
            </div>
            <div>
                Route: RUB > USDT
            </div>
        </div>
    );
};

export default Swap;
