import React, { useState } from 'react';
import RmbLogo from './path/to/rmbLogo'; // Иконка RMB
import UsdLogo from './path/to/usdLogo'; // Иконка USD
import './swap.module.css'; // Импортируем модульные стили

const Swap = ({ ruB, conversionResult }) => {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [position, setPosition] = useState({ x: 0, y: 0 });

    const handleRightClick = (event) => {
        event.preventDefault(); // предотвращает стандартное контекстное меню
        setIsDropdownOpen(true);
        setPosition({ x: event.clientX, y: event.clientY });
    };

    const handleClose = () => {
        setIsDropdownOpen(false);
    };

    return (
        <div style={{ position: 'relative' }}>
            {/* Строка To остается неизменной */}
            <div>
                To: {ruB}
            </div>

            {/* Строка From с новыми данными */}
            <div>
                From: 
                <div onContextMenu={handleRightClick} style={{ display: 'inline-block', cursor: 'pointer' }}>
                    <RmbLogo className="currency-icon" /> {/* Иконка RMB */}
                    RMB
                </div>

                {conversionResult} {/* Результат конвертации */}

                {/* Выпадающий список */}
                {isDropdownOpen && (
                    <div
                        style={{
                            position: 'absolute',
                            top: position.y,
                            left: position.x,
                            backgroundColor: 'white',
                            border: '1px solid gray',
                            zIndex: 1000,
                        }}
                        onMouseLeave={handleClose} // закрывает меню при выходе мыши
                    >
                        <UsdLogo className="currency-icon" style={{ width: '20px', height: '20px' }} />
                        <span> USD</span>
                    </div>
                )}
            </div>

            <div>
                Цена: 14.11 RUB per 1 RMB
            </div>
            <div>
                Route: RUB > USDT
            </div>
        </div>
    );
};

export default Swap;
